mapper.select:
select count(*) cnt 
from product p join product_attachment pa
on p.product_no = pa.product_no

	pa.thumbnail = 'Y'

		and p.${searchType} like '%${searchKeyword}%'


		and p.sido like #{sido}


		and p.sigungu like #{sigungu}


		and p.dong like #{dong}

select p.*, pa.photo 
from product p join product_attachment pa
on p.product_no = pa.product_no

	pa.thumbnail = 'Y'

		and p.${searchType} like '%${searchKeyword}%'


		and p.sido like #{sido}


		and p.sigungu like #{sigungu}


		and p.dong like #{dong}
