mapper.update:
update member 
set member_name=#{afterName}
where member_id=#{memberId}

update member 
set phone=#{afterPhone}
where member_id=#{memberId}

update member 
set email=#{email}
where member_id=#{memberId}

mapper.select:
select * from point_status

	member_id=#{memberId}


	and
	status=#{option}



	and
	reg_date between to_date(#{start}, 'yyyymmdd') and to_date(#{end},'yyyymmddhh24miss')

select count(*) from point_status 

		member_id=#{memberId}


		and
		status=#{option}



		and
		reg_date between to_date(#{start}, 'yyyymmdd') and to_date(#{end},'yyyymmddhh24miss')
